import React, { Component } from 'react';
import Todo from './components/todo';
import AddToDo from './components/addToDo'
import Header from './components/layout/Header'
import './App.css';
class App extends Component {
  state = { 
    todos: [{
        id: 1,
        title: 'eat breakfast',
        completed: true
      },
      {
        id: 2,
        title: 'eat lunch',
        completed: true       
      },
      {
        id: 3,
        title: 'eat dinner',
        completed: true        
      }
    ]
   }
   // Toggle Complete
   markComplete = (id) => {
    this.setState({ todos: this.state.todos.map(todolist => {
      if(todolist.id === id){
        todolist.completed = !todolist.completed
      }
      return (todolist)
    })})
}
deleteButton = (id) => {
  console.log('delete the specific todo list item', id)
  this.setState({todos: [...this.state.todos.filter(todolist => todolist.id !== id)]});
}
  render() { 
    console.log(this.state.todos)
    return ( 
      <div >
        <Header/>
        <AddToDo/>
        <h2><Todo todolist = {this.state.todos} markComplete = {this.markComplete} passDelete = {this.deleteButton}/></h2>
      </div>
     );
  }
}

export default App;
