import React, { Component } from 'react';
import './App.css';
class App extends Component {
  state = { 
    todo: [{
        id: 1,
        title: 'eat breakfast',
        completed: 'true'
      },
      {
        id: 2,
        title: 'eat lunch',
        completed: 'true'        
      },
      {
        id: 3,
        title: 'eat dinner',
        completed: 'true'        
      }
    ]
   }
  render() { 
    console.log(this.state.todo)
    return ( 
      <div className = "App">
        <h1>{this.state.todo.title}</h1>
      </div>
     );
  }
}

export default App;
