import React, { Component } from 'react';
import Todo from './components/todo';
import './App.css';
class App extends Component {
  state = { 
    todos: [{
        id: 1,
        title: 'eat breakfast',
        completed: true
      },
      {
        id: 2,
        title: 'eat lunch',
        completed: true       
      },
      {
        id: 3,
        title: 'eat dinner',
        completed: true        
      }
    ]
   }
   markComplete = (id) => {
    this.setState({ todos: this.state.todos.map(todolist => {
      if(todolist.id === id){
        todolist.completed = !todolist.completed
      }
      return (todolist)
    })})
}
  render() { 
    console.log(this.state.todos)
    return ( 
      <div >
        <h2><Todo todolist = {this.state.todos} markComplete = {this.markComplete}/></h2>
      </div>
     );
  }
}

export default App;
