import React, { Component } from 'react'
import PropTypes from 'prop-types';

export class ToDoItems extends Component {
    getStyle = () =>{
        return {
            color: '#FF3374',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            backgroundColor: '#f4f4f4',
            textDecoration: this.props.todolist.completed === 'false' ? 'line-through': 'none'
        }
    }
    render() {
        return (
            <div style = { this.getStyle() }>
                <p>
                    <input type = "checkbox" onChange = {this.props.passMarkComplete}></input>
                    {this.props.passTodolist.title}
                </p>
            </div>
        )
    }
}
// PropTypes
ToDoItems.propTypes ={
    passTodolist : PropTypes.object.isRequired
}
// const itemStyle = {
